class LevusOnslide{constructor(s){this.slider=document.querySelector(s),this.slides=this.slider.querySelectorAll("article"),this.left=this.slider.querySelector("#levus-left"),this.right=this.slider.querySelector("#levus-right"),this.length=this.slides.length-1,this.first=this.slides[0],this.cnt=0}click(){this.left.addEventListener("click",()=>{setTimeout(()=>{this.slides.forEach(s=>s.removeAttribute("class")),this.cnt<this.length?this.cnt++:this.cnt=0,this.slides[this.cnt].classList.add("show")},500)}),this.right.addEventListener("click",()=>{setTimeout(()=>{this.slides.forEach(s=>s.removeAttribute("class")),0===this.cnt?this.cnt=this.length:this.cnt--,this.slides[this.cnt].classList.add("show")},500)})}arrow(){this.left.classList.add("hide"),this.right.classList.add("hide"),this.slider.addEventListener("mouseover",()=>{this.left.removeAttribute("class"),this.right.removeAttribute("class")}),this.slider.addEventListener("mouseout",()=>{this.left.classList.add("hide"),this.right.classList.add("hide")})}autoScroll(){setInterval(()=>{this.slides.forEach(s=>s.removeAttribute("class")),this.cnt<this.length?this.cnt++:this.cnt=0,this.slides[this.cnt].classList.add("show")},8e3)}init(){this.click(),this.first.classList.add("show"),this.arrow(),this.autoScroll()}}

new LevusOnslide("#levus-slider").init();
